GlobalChangeSpec.CreateProcedureSQL:
create procedure dbo.USR_USP_GLOBALCHANGE_ADDNAMEFORMATTYPECODES ( @CHANGEAGENTID uniqueidentifier = null, @ASOF as datetime = null, @NUMBERADDED int = 0 output, @NUMBEREDITED int = 0 output, @NUMBERDELETED int = 0 output ) as set nocount off;
declare @CURRENTDATE datetime set @CURRENTDATE = getdate(); set @NUMBERADDED = 0; set @NUMBEREDITED = 0; set @NUMBERDELETED = 0;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;
begin try IF OBJECT_ID('tempdb..#tmpenftc') IS NOT NULL DROP TABLE #tmpenftc
SELECT * INTO #tmpenftc FROM (SELECT 'Household Addressee Formal' AS NFTCNAME, newid() as NFTCID, 1 as ACTIVE, 0 as SEQUENCE UNION ALL SELECT 'Household Addressee Informal' AS NFTCNAME, newid() as NFTCID, 1 as ACTIVE, 0 as SEQUENCE UNION ALL SELECT 'Household Salutation Formal' AS NFTCNAME, newid() as NFTCID, 1 as ACTIVE, 0 as SEQUENCE UNION ALL SELECT 'Household Salutation Informal' AS NFTCNAME, newid() as NFTCID, 1 as ACTIVE, 0 as SEQUENCE UNION ALL SELECT 'Individual Addressee Formal' AS NFTCNAME, newid() as NFTCID, 1 as ACTIVE, 0 as SEQUENCE UNION ALL SELECT 'Individual Addressee Informal' AS NFTCNAME, newid() as NFTCID, 1 as ACTIVE, 0 as SEQUENCE UNION ALL SELECT 'Individual Salutation Formal' AS NFTCNAME, newid() as NFTCID, 1 as ACTIVE, 0 as SEQUENCE UNION ALL SELECT 'Individual Salutation Informal' AS NFTCNAME, newid() as NFTCID, 1 as ACTIVE, 0 as SEQUENCE) nftctbl
INSERT INTO dbo.NAMEFORMATTYPECODE (ID, DESCRIPTION, ACTIVE, SEQUENCE, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED) SELECT NFTCID, NFTCNAME, ACTIVE, SEQUENCE, @CHANGEAGENTID, @CHANGEAGENTID, @CURRENTDATE, @CURRENTDATE FROM #tmpenftc WHERE NFTCNAME NOT IN (SELECT DESCRIPTION FROM dbo.NAMEFORMATTYPECODE)
SET @NUMBERADDED = @@ROWCOUNT
IF OBJECT_ID('tempdb..#tmpenftc') IS NOT NULL DROP TABLE #tmpenftc end try
begin catch IF OBJECT_ID('tempdb..#tmpenftc') IS NOT NULL DROP TABLE #tmpenftc exec dbo.USP_RAISE_ERROR; return 1; end catch
