create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_EDITTITLEPROPERTIES ( @DATALOADED bit = 0 output, @TSLONG bigint = 0 output, @TITLECOLFIELD xml = null output ) as
set nocount on;
-- be sure to set these, in case the select returns no rows set @DATALOADED = 0 set @TSLONG = 0
SELECT @TITLECOLFIELD = (SELECT tc.ID as TITLECODEID, coalesce(tp.ISSPECIALTITLE, 1) as ISSPECIAL, coalesce(tp.ISMARITALTITLE, 0) as ISMARITAL, coalesce(tp.ISRELIGIOUSTITLE, 0) as ISRELIGIOUS, coalesce(tp.GENDER, 0) as GENDER FROM dbo.TITLECODE tc LEFT OUTER JOIN dbo.USR_TITLEPROPERTIES tp ON tc.ID = tp.TITLECODEID ORDER BY tc.DESCRIPTION FOR XML PATH('ITEM'), ROOT('TITLECOLFIELD'))
SET @DATALOADED = 1
return 0;
